import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
    standalone: false,

  template: `
    <!-- ===== Brand Header (black) ===== -->
    <header class="header">
      <div class="brand">
        <!-- Wordmark rendered as text so you don't see white boxes -->
        <span class="kpmg" aria-label="KPMG">KPMG</span>
        <span class="vbar" aria-hidden="true"></span>
        <span class="product">Access Management</span>
      </div>
    </header>

    <!-- ===== Tabs Row (white with thin divider) ===== -->
    <nav class="tabbar">
      <div class="tabs">
        <!-- 3×3 apps grid icon (muted gray) to the left of Dashboard -->
        <span class="apps" aria-label="Apps">
          <svg viewBox="0 0 24 24" width="18" height="18" focusable="false" aria-hidden="true">
            <circle cx="5"  cy="5"  r="1.6"></circle>
            <circle cx="12" cy="5"  r="1.6"></circle>
            <circle cx="19" cy="5"  r="1.6"></circle>
            <circle cx="5"  cy="12" r="1.6"></circle>
            <circle cx="12" cy="12" r="1.6"></circle>
            <circle cx="19" cy="12" r="1.6"></circle>
            <circle cx="5"  cy="19" r="1.6"></circle>
            <circle cx="12" cy="19" r="1.6"></circle>
            <circle cx="19" cy="19" r="1.6"></circle>
          </svg>
        </span>

        <a routerLink="/dashboard"
           routerLinkActive="active"
           [routerLinkActiveOptions]="{ exact: true }"
           class="tab">Dashboard</a>

        <a routerLink="/applications-features" routerLinkActive="active" class="tab">
          Application &amp; Features
        </a>
        <a routerLink="/groups" routerLinkActive="active" class="tab">Groups</a>
        <a routerLink="/roles" routerLinkActive="active" class="tab">Roles</a>
        <a routerLink="/users" routerLinkActive="active" class="tab">Users</a>
        <a routerLink="/templates" routerLinkActive="active" class="tab">Templates</a>
        <a routerLink="/audit-log" routerLinkActive="active" class="tab">Audit Log</a>
      </div>
    </nav>

    <!-- ===== Routed content ===== -->
    <main class="container">
      <router-outlet></router-outlet>
    </main>
  `,
  styles: [`
    :host {
      display: block;
      background: #fff;
      color: #111827;
      font-family: 'Segoe UI', Roboto, system-ui, -apple-system, Arial, sans-serif;
      font-size: 16px;                 /* bigger base text */
      line-height: 1.45;
    }

    /* ===== Header ===== */
    .header {
      background: #1f1f1f;            /* blackish like screenshot */
      color: #fff;
      padding: 10px 20px;
      border-top-left-radius: 8px;
      border-top-right-radius: 8px;
    }
    .brand { display: flex; align-items: center; gap: 12px; }

    /* "KPMG" wordmark rendered as white italic text */
    .kpmg {
      color: #fff;
      font-style: italic;
      font-weight: 800;
      font-size: 20px;                /* matches ref */
      letter-spacing: 0.3px;
      text-transform: uppercase;
      line-height: 1;
      display: inline-block;
      /* slight “caps” feel, optional; remove if you have official asset */
      text-shadow: 0 0 .01px rgba(255,255,255,.9);
    }

    .vbar {
      width: 1px; height: 22px;
      background: rgba(255,255,255,.35); /* thin light divider */
      display: inline-block;
      border-radius: 1px;
    }

    .product {
      font-weight: 700;
      font-size: 20px;                 /* larger product name like ref */
      letter-spacing: .2px;
    }

    /* ===== Tabs row ===== */
    .tabbar {
      background: #fff;
      border-bottom: 1px solid #e5e7eb; /* thin divider line */
    }
    .tabs {
      height: 48px;
      display: flex; align-items: center;
      gap: 26px;
      padding: 0 20px;
    }

    /* 3×3 icon muted gray */
    .apps { display: inline-flex; align-items:center; margin-right: 2px; }
    .apps svg { fill: #9ca3af; }       /* gray-400 */

    .tab {
      text-decoration: none;
      color: #1f2937;                  /* gray-800 */
      font-weight: 500;
      font-size: 15.5px;               /* slightly larger tab labels */
      padding-bottom: 6px;
      border-bottom: 3px solid transparent; /* underline appears when active */
      transition: color .15s ease;
    }
    .tab:hover { color: #111827; }
    .tab.active {
      color: #111827;
      font-weight: 600;
      border-bottom-color: #d1d5db;    /* light gray underline (NOT blue) */
    }

    /* ===== Page area ===== */
    .container { padding: 20px; }

    /* Optional helpers if your pages use a subheader */
    :host ::ng-deep .subheader {
      background: #f3f4f6;
      border-bottom: 1px solid #e5e7eb;
      padding: 18px 20px;
      margin: 0 -20px 12px;
    }
    :host ::ng-deep .page-title {
      margin: 0;
      font-size: 24px;
      font-weight: 800;
      color: #111827;
    }
  `]
})
export class AppComponent {}
