import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
    standalone: false,

  template: `
    <header class="header" role="banner">
      <div class="brand">
        <span class="kpmg-block" aria-hidden="true">
          <!-- simplified decorative KPMG mark made inline to avoid missing asset issues -->
          <svg class="kpmg-logo" viewBox="0 0 180 40" width="140" height="28" focusable="false" aria-hidden="true">
            <g fill="none" stroke="#ffffff" stroke-width="1.4">
              <rect x="0"   y="6" width="36" height="26" rx="2"></rect>
              <rect x="38"  y="6" width="36" height="26" rx="2"></rect>
              <rect x="76"  y="6" width="36" height="26" rx="2"></rect>
              <rect x="114" y="6" width="36" height="26" rx="2"></rect>
            </g>
            <text x="156" y="24"
                  fill="#ffffff"
                  font-size="18"
                  font-weight="800"
                  font-family="Segoe UI, Roboto, Arial, sans-serif">
              KPMG
            </text>
          </svg>
        </span>

        <span class="vbar" aria-hidden="true"></span>

        <span class="product">Access Management</span>
      </div>
    </header>

    <nav class="tabbar" role="navigation" aria-label="Main tabs">
      <div class="tabs">
        <span class="apps" aria-hidden="true">
          <svg viewBox="0 0 24 24" width="18" height="18" focusable="false" aria-hidden="true">
            <g fill="#9CA3AF">
              <circle cx="5"  cy="5"  r="1.6"></circle>
              <circle cx="12" cy="5"  r="1.6"></circle>
              <circle cx="19" cy="5"  r="1.6"></circle>
              <circle cx="5"  cy="12" r="1.6"></circle>
              <circle cx="12" cy="12" r="1.6"></circle>
              <circle cx="19" cy="12" r="1.6"></circle>
              <circle cx="5"  cy="19" r="1.6"></circle>
              <circle cx="12" cy="19" r="1.6"></circle>
              <circle cx="19" cy="19" r="1.6"></circle>
            </g>
          </svg>
        </span>

        <a routerLink="/dashboard" routerLinkActive="active" [routerLinkActiveOptions]="{ exact: true }" class="tab">Dashboard</a>
        <a routerLink="/applications-features" routerLinkActive="active" class="tab">Application &amp; Features</a>
        <a routerLink="/groups" routerLinkActive="active" class="tab">Groups</a>
        <a routerLink="/roles" routerLinkActive="active" class="tab">Roles</a>
        <a routerLink="/users" routerLinkActive="active" class="tab">Users</a>
        <a routerLink="/templates" routerLinkActive="active" class="tab">Templates</a>
        <a routerLink="/audit-log" routerLinkActive="active" class="tab">Audit Log</a>
      </div>
    </nav>

    <main class="container" role="main">
      <router-outlet></router-outlet>
    </main>
  `,
  styles: [`
    :host {
      display: block;
      min-height: 100vh;
      background: #ffffff;
      color: #111827;
      font-family: "Segoe UI", Roboto, system-ui, -apple-system, Arial, sans-serif;
      -webkit-font-smoothing: antialiased;
    }

    /* header */
    .header {
      background: #000000;
      color: #fff;
      padding: 12px 20px;
      box-shadow: 0 1px 0 rgba(0,0,0,0.06);
    }

    .brand {
      display: flex;
      align-items: center;
      gap: 12px;
    }

    .kpmg-logo { display:block; height:28px; }

    .vbar {
      width:1px; height:28px; background: rgba(255,255,255,0.25); display:inline-block; margin-left:6px; margin-right:10px;
    }

    .product {
      font-size:20px; font-weight:700; letter-spacing:0.2px; color:#fff;
    }

    /* tabs */
    .tabbar {
      background:#ffffff;
      border-bottom:1px solid #e6e6e6;
    }

    .tabs {
      height:56px;
      display:flex;
      align-items:center;
      gap:22px;
      padding:0 20px;
      max-width:1200px;
      margin:0 auto;
    }

    .apps { display:inline-flex; align-items:center; margin-right:4px; }

    .tab {
      color:#374151;
      text-decoration:none;
      font-size:15px;
      font-weight:500;
      padding:10px 0;
      border-bottom:3px solid transparent;
      transition:color .12s ease, border-color .12s ease;
    }

    .tab:hover { color:#111827; }

    /* match highlighted Application & Features (make it visually stronger) */
    .tab.active {
      color:#00338d;
      font-weight:600;
      border-bottom-color:#00338d;
    }

    /* content area */
    .container {
      max-width:1200px;
      margin:20px auto;
      padding:20px;
      background:#fff;
      min-height:320px;
      border-radius:6px;
      box-shadow: 0 1px 2px rgba(16,24,40,0.04);
    }

    /* responsive */
    @media (max-width:700px) {
      .tabs { gap:12px; padding:0 12px; }
      .product { font-size:18px; }
    }
  `]
})
export class AppComponent {}
