import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
    standalone: false,

  template: `
    <!-- Top Header -->
    <header class="header">
      <div class="logo">
        <img src="assets/kpmg.svg" alt="KPMG" />
        <span class="divider">|</span>
        <span class="title">Access Management</span>
      </div>
    </header>

    <!-- Navigation Tabs -->
    <nav class="tabbar">
      <div class="tabs">
        <a routerLink="/dashboard" routerLinkActive="active" [routerLinkActiveOptions]="{ exact: true }" class="tab">
          <span class="icon">☰</span> Dashboard
        </a>
        <a routerLink="/applications-features" routerLinkActive="active" class="tab">Application &amp; Features</a>
        <a routerLink="/groups" routerLinkActive="active" class="tab">Groups</a>
        <a routerLink="/roles" routerLinkActive="active" class="tab">Roles</a>
        <a routerLink="/users" routerLinkActive="active" class="tab">Users</a>
        <a routerLink="/templates" routerLinkActive="active" class="tab">Templates</a>
        <a routerLink="/audit-log" routerLinkActive="active" class="tab">Audit Log</a>
      </div>
    </nav>

    <!-- Page Content -->
    <main class="container">
      <router-outlet></router-outlet>
    </main>
  `,
  styles: [`
    :host {
      display: block;
      min-height: 100vh;
      background-color: #ffffff;
      color: #333;
      font-family: 'Segoe UI', Roboto, sans-serif;
    }

    /* Header */
    .header {
      background-color: #1a1a1a;
      color: white;
      display: flex;
      align-items: center;
      padding: 10px 24px;
      border-top-left-radius: 8px;
      border-top-right-radius: 8px;
    }

    .logo {
      display: flex;
      align-items: center;
      gap: 10px;
      font-size: 18px;
    }

    .logo img {
      height: 28px;
    }

    .divider {
      font-weight: 600;
      font-size: 18px;
      color: #ffffffb3;
    }

    .title {
      font-size: 18px;
      font-weight: 500;
      color: #ffffff;
    }

    /* Tabs Bar */
    .tabbar {
      background-color: #f9f9f9;
      border-bottom: 1px solid #dcdcdc;
      padding: 0 24px;
    }

    .tabs {
      display: flex;
      align-items: center;
      gap: 32px;
      padding: 12px 0;
      font-weight: 500;
    }

    .tab {
      text-decoration: none;
      color: #333;
      padding-bottom: 6px;
      border-bottom: 3px solid transparent;
      transition: all 0.2s ease-in-out;
      display: flex;
      align-items: center;
      gap: 6px;
    }

    .tab:hover {
      color: #0056b3;
    }

    .tab.active {
      color: #000;
      font-weight: 600;
      border-bottom: 3px solid #00338d;
    }

    .icon {
      font-size: 16px;
      color: #555;
    }

    .container {
      padding: 24px;
    }
  `]
})
export class AppComponent {}
