import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
    standalone: false,

  template: `
    <!-- ===== Brand Header (black) ===== -->
    <header class="header">
      <div class="brand">
        <!-- Four outlined boxes (stroke only, no fill) -->
        <svg class="kpmg-boxes" viewBox="0 0 60 22" width="60" height="22" aria-hidden="true" focusable="false">
          <rect x="0"   y="2" width="13" height="18" rx="2" ry="2"></rect>
          <rect x="16"  y="2" width="13" height="18" rx="2" ry="2"></rect>
          <rect x="32"  y="2" width="13" height="18" rx="2" ry="2"></rect>
          <rect x="48"  y="2" width="13" height="18" rx="2" ry="2"></rect>
        </svg>

        <!-- Italic KPMG word -->
        <span class="kpmg-word">KPMG</span>

        <!-- Thin vertical divider -->
        <span class="vbar" aria-hidden="true"></span>

        <!-- Bigger product title -->
        <span class="product">Access Management</span>
      </div>
    </header>

    <!-- ===== Tabs row (white with light-gray active underline) ===== -->
    <nav class="tabbar">
      <div class="tabs">
        <!-- 3×3 apps grid icon (muted gray) -->
        <span class="apps" aria-label="Apps">
          <svg viewBox="0 0 24 24" width="18" height="18" focusable="false" aria-hidden="true">
            <circle cx="5"  cy="5"  r="1.6"></circle>
            <circle cx="12" cy="5"  r="1.6"></circle>
            <circle cx="19" cy="5"  r="1.6"></circle>
            <circle cx="5"  cy="12" r="1.6"></circle>
            <circle cx="12" cy="12" r="1.6"></circle>
            <circle cx="19" cy="12" r="1.6"></circle>
            <circle cx="5"  cy="19" r="1.6"></circle>
            <circle cx="12" cy="19" r="1.6"></circle>
            <circle cx="19" cy="19" r="1.6"></circle>
          </svg>
        </span>

        <a routerLink="/dashboard" routerLinkActive="active" [routerLinkActiveOptions]="{ exact: true }" class="tab">Dashboard</a>
        <a routerLink="/applications-features" routerLinkActive="active" class="tab">Application &amp; Features</a>
        <a routerLink="/groups" routerLinkActive="active" class="tab">Groups</a>
        <a routerLink="/roles" routerLinkActive="active" class="tab">Roles</a>
        <a routerLink="/users" routerLinkActive="active" class="tab">Users</a>
        <a routerLink="/templates" routerLinkActive="active" class="tab">Templates</a>
        <a routerLink="/audit-log" routerLinkActive="active" class="tab">Audit Log</a>
      </div>
    </nav>

    <!-- Routed content -->
    <main class="container">
      <router-outlet></router-outlet>
    </main>
  `,
  styles: [`
    :host{
      display:block;
      background:#fff;
      color:#111827;
      font-family:'Segoe UI', Roboto, system-ui, -apple-system, Arial, sans-serif;
      font-size:16.5px;  /* slightly larger base font */
      line-height:1.45;
    }

    /* ===== Header ===== */
    .header{
      background:#1f1f1f;
      color:#fff;
      padding:10px 20px;
      border-top-left-radius:8px;
      border-top-right-radius:8px;
    }
    .brand{ display:flex; align-items:center; gap:10px; }

    /* Force outlined boxes (no fill), crisp stroke */
    .kpmg-boxes { display:inline-block; vertical-align:middle; }
    .kpmg-boxes rect{
      fill: transparent !important;
      stroke: #ffffff !important;
      stroke-width: 1.8;
      vector-effect: non-scaling-stroke;
      shape-rendering: geometricPrecision;
    }

    .kpmg-word{
      font-style: italic;
      font-weight: 800;
      font-size: 20px;   /* matches reference size */
      letter-spacing: .3px;
      margin-left: 4px;
    }

    .vbar{
      width:1px; height:24px;
      background: rgba(255,255,255,.35);
      display:inline-block; border-radius:1px;
      margin:0 10px 0 10px;
    }

    .product{
      font-weight: 800;
      font-size: 24px;   /* bigger “Access Management” */
      color:#fff;
      letter-spacing:.2px;
    }

    /* ===== Tabs ===== */
    .tabbar{ background:#fff; border-bottom:1px solid #e5e7eb; }
    .tabs{ height:48px; display:flex; align-items:center; gap:26px; padding:0 20px; }

    .apps{ display:inline-flex; align-items:center; margin-right:2px; }
    .apps svg{ fill:#9ca3af; } /* muted gray */

    .tab{
      text-decoration:none; color:#1f2937;
      font-weight:500; font-size:15.5px;
      padding-bottom:6px;
      border-bottom:3px solid transparent;
      transition:color .15s ease;
    }
    .tab:hover{ color:#111827; }
    .tab.active{
      color:#111827;
      font-weight:600;
      border-bottom-color:#d1d5db; /* light-gray underline */
    }

    .container{ padding:20px; }
  `]
})
export class AppComponent {}
