import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

interface MenuItem {
  label: string;
  path: string;
}

@Component({
  selector: 'app-root',
  standalone: false,
  template: `
    <!-- ===== Header (like screenshot) ===== -->
    <header class="header">
      <div class="brand">
        <img src="assets/KPMGLOGO.png" alt="KPMG" class="logo" />
        <span class="vline"></span>
        <span class="product">Access Management</span>
      </div>
    </header>

    <!-- ===== Menu bar ===== -->
    <nav class="tabbar">
      <div class="tabs">
        <span class="apps" aria-label="Apps">
          <svg viewBox="0 0 24 24" width="22" height="22" focusable="false" aria-hidden="true">
            <circle cx="5" cy="5" r="2"></circle>
            <circle cx="12" cy="5" r="2"></circle>
            <circle cx="19" cy="5" r="2"></circle>
            <circle cx="5" cy="12" r="2"></circle>
            <circle cx="12" cy="12" r="2"></circle>
            <circle cx="19" cy="12" r="2"></circle>
            <circle cx="5" cy="19" r="2"></circle>
            <circle cx="12" cy="19" r="2"></circle>
            <circle cx="19" cy="19" r="2"></circle>
          </svg>
        </span>

        <a *ngFor="let item of menuItems"
           class="tab"
           [routerLink]="item.path"
           routerLinkActive="active"
           [routerLinkActiveOptions]="{ exact: true }">
          {{ item.label }}
        </a>
      </div>
    </nav>

    <!-- ===== Routed content ===== -->
    <main class="container">
      <router-outlet></router-outlet>
    </main>
  `,
  styles: [`
    :host {
      display: block;
      background: #ffffff;
      color: #111827;
      font-family: 'Segoe UI', Roboto, system-ui, -apple-system, Arial, sans-serif;
    }

    /* ===== Header ===== */
    .header {
      background: #1f1f1f;
      color: #fff;
      padding: 18px 28px;
      border-top-left-radius: 10px;
      border-top-right-radius: 10px;
    }
    .brand {
      display: flex; align-items: center; gap: 14px;
    }
    .logo { height: 46px; width: auto; object-fit: contain; }
    .vline { width: 1px; height: 34px; background: rgba(255,255,255,.4); }
    .product { font-size: 26px; font-weight: 700; letter-spacing: .2px; }

    /* ===== Menu bar (like screenshot) ===== */
    .tabbar {
      background: #fff;
      border-bottom: 1px solid #e6e6e6;
    }
    .tabs {
      display: flex; align-items: center;
      gap: 36px;                         /* space between items */
      height: 64px;                      /* menu height */
      padding: 0 24px;
      font-size: 18px;                   /* larger font */
      font-weight: 600;
    }
    .apps { display: inline-flex; align-items: center; margin-right: 8px; }
    .apps svg { fill: #9ca3af; }

    .tab {
      color: #2a2f36;
      text-decoration: none;
      padding-bottom: 10px;
      border-bottom: 3px solid transparent;
      transition: color .2s, border-color .2s;
    }
    .tab:hover { color: #000; }

    /* Active exactly like the screenshot: bold, black text, black underline */
    .tab.active {
      color: #000;
      font-weight: 700;
      border-bottom-color: #000;
    }

    /* ===== Page area ===== */
    .container { padding: 24px; }
  `]
})
export class AppComponent implements OnInit {
  menuItems: MenuItem[] = [];

  constructor(private http: HttpClient) {}

  ngOnInit(): void {
    this.http.get<MenuItem[]>('assets/menu.json')
      .subscribe({
        next: (data) => this.menuItems = data,
        error: () => this.menuItems = []   // fail-safe
      });
  }
}
