import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
    standalone: false,

  template: `
    <!-- Brand header -->
    <header class="header">
      <div class="brand">
        <img class="logo" src="assets/kpmg-wordmark-white.svg" alt="KPMG" />
        <span class="vbar" aria-hidden="true"></span>
        <span class="product">Access Management</span>
      </div>
    </header>

    <!-- Top tabs (white background, thin divider, gray active underline) -->
    <nav class="tabbar">
      <div class="tabs">
        <!-- 3×3 apps icon (like your reference) -->
        <span class="apps" aria-label="Apps">
          <svg viewBox="0 0 24 24" width="18" height="18" focusable="false" aria-hidden="true">
            <circle cx="5"  cy="5"  r="1.6"></circle>
            <circle cx="12" cy="5"  r="1.6"></circle>
            <circle cx="19" cy="5"  r="1.6"></circle>
            <circle cx="5"  cy="12" r="1.6"></circle>
            <circle cx="12" cy="12" r="1.6"></circle>
            <circle cx="19" cy="12" r="1.6"></circle>
            <circle cx="5"  cy="19" r="1.6"></circle>
            <circle cx="12" cy="19" r="1.6"></circle>
            <circle cx="19" cy="19" r="1.6"></circle>
          </svg>
        </span>

        <a routerLink="/dashboard"
           routerLinkActive="active"
           [routerLinkActiveOptions]="{ exact: true }"
           class="tab">Dashboard</a>

        <a routerLink="/applications-features" routerLinkActive="active" class="tab">
          Applications &amp; Features
        </a>

        <a routerLink="/groups" routerLinkActive="active" class="tab">Groups</a>
        <a routerLink="/roles" routerLinkActive="active" class="tab">Roles</a>
        <a routerLink="/users" routerLinkActive="active" class="tab">Users</a>
        <a routerLink="/templates" routerLinkActive="active" class="tab">Templates</a>
        <a routerLink="/audit-log" routerLinkActive="active" class="tab">Audit Log</a>
      </div>
    </nav>

    <!-- Routed content -->
    <main class="container">
      <router-outlet></router-outlet>
    </main>
  `,
  styles: [`
    :host {
      display: block;
      background: #ffffff;
      color: #111827;
      font-family: 'Segoe UI', Roboto, system-ui, -apple-system, Arial, sans-serif;
      /* ↑ bump base size so all app text is larger */
      font-size: 16px;
      line-height: 1.45;
    }

    /* ===== Header ===== */
    .header {
      background: #1f1f1f;             /* near-black like screenshot */
      color: #fff;
      padding: 12px 20px;
      border-top-left-radius: 8px;
      border-top-right-radius: 8px;
    }
    .brand { display: flex; align-items: center; gap: 12px; }
    .logo {
      height: 22px;
      /* If your SVG isn't already white, this forces it: */
      filter: brightness(0) invert(1);
    }
    .vbar {
      width: 1px; height: 22px; background: rgba(255,255,255,.35);
      display: inline-block; border-radius: 1px;
    }
    .product {
      font-weight: 700;
      font-size: 20px;                 /* ↑ larger title like reference */
      letter-spacing: .2px;
    }

    /* ===== Tabs row ===== */
    .tabbar {
      background: #fff;
      border-bottom: 1px solid #e5e7eb; /* thin divider */
    }
    .tabs {
      height: 48px;
      display: flex; align-items: center;
      gap: 24px;
      padding: 0 20px;
    }

    /* 3×3 apps icon at far left (muted like screenshot) */
    .apps { display: inline-flex; align-items:center; }
    .apps svg { fill: #9ca3af; }        /* gray-400 */

    .tab {
      text-decoration: none;
      color: #1f2937;                   /* gray-800 */
      font-weight: 500;
      padding-bottom: 6px;
      border-bottom: 3px solid transparent; /* active underline sits here */
      /* ↑ underline is light gray per screenshot, not blue */
    }
    .tab:hover { color: #111827; }
    .tab.active {
      color: #111827;                   /* keep text dark */
      border-bottom-color: #d1d5db;     /* gray-300 underline like ref */
      font-weight: 600;
    }

    /* ===== Routed page area ===== */
    .container { padding: 20px; }

    /* Optional: if your pages use .subheader + .page-title like the mock,
       these bump sizes to match the screenshot proportions. */
    :host ::ng-deep .subheader {
      background: #f3f4f6;
      border-bottom: 1px solid #e5e7eb;
      padding: 18px 20px;
      margin: 0 -20px 12px;      /* stretch to edges under the tab bar */
    }
    :host ::ng-deep .page-title {
      margin: 0;
      font-size: 24px;           /* ↑ page heading size */
      font-weight: 800;
      color: #111827;
    }
  `]
})
export class AppComponent {}
