import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  standalone: false,
  template: `
    <header class="header">
      <div class="logo">
        <img src="assets/kpmg.svg" alt="KPMG" />
        <span class="divider">|</span>
        <span class="title">Access Management</span>
      </div>
    </header>

    <nav class="tabbar">
      <div class="tabs">
        <a routerLink="/dashboard" routerLinkActive="active" class="tab" [routerLinkActiveOptions]="{ exact: true }">Dashboard</a>
        <a routerLink="/applications-features" routerLinkActive="active" class="tab">Applications &amp; Features</a>
        <a routerLink="/groups" routerLinkActive="active" class="tab">Groups</a>
        <a routerLink="/roles" routerLinkActive="active" class="tab">Roles</a>
        <a routerLink="/users" routerLinkActive="active" class="tab">Users</a>
        <a routerLink="/templates" routerLinkActive="active" class="tab">Templates</a>
        <a routerLink="/audit-log" routerLinkActive="active" class="tab">Audit Log</a>
      </div>
    </nav>

    <main class="container">
      <router-outlet></router-outlet>
    </main>
  `,
  styles: [`
    :host {
      display: block;
      background-color: #f5f5f5;
      color: #333;
      font-family: 'Segoe UI', Roboto, sans-serif;
      min-height: 100vh;
    }

    .header {
      display: flex;
      align-items: center;
      padding: 12px 24px;
      background-color: #00338d;
      color: white;
    }

    .logo {
      display: flex;
      align-items: center;
      gap: 12px;
    }

    .logo img {
      height: 32px;
    }

    .divider {
      font-size: 20px;
      font-weight: bold;
    }

    .title {
      font-size: 20px;
      font-weight: 500;
    }

    .tabbar {
      background-color: #e6e6e6;
      padding: 0 24px;
      box-shadow: inset 0 -1px 0 #ccc;
    }

    .tabs {
      display: flex;
      gap: 24px;
      padding: 12px 0;
    }

    .tab {
      text-decoration: none;
      color: #333;
      font-weight: 500;
      padding-bottom: 4px;
      border-bottom: 2px solid transparent;
      transition: color 0.2s ease;
    }

    .tab.active {
      color: #00338d;
      border-bottom-color: #00338d;
      font-weight: 600;
    }

    .tab:hover {
      color: #0055cc;
    }

    .container {
      padding: 24px;
    }
  `]
})
export class AppComponent {}
